using System;
using System.Collections.Generic;
using System.Linq;
using System.Text;
using System.Threading.Tasks;

namespace Day2
{
    class Program
    {

        public static int[][] Keypad;
        public static int CurrentPositionX;
        public static int CurrentPositionY;

        static void Main(string[] args)
        {
            Keypad= new int[3][];
            Keypad[0] = new int[3] {1, 2, 3};
            Keypad[1] = new int[3] {4, 5, 6};
            Keypad[2] = new int[3] {7, 8, 9};
            CurrentPositionX = 1;
            CurrentPositionY = 1;


            string exampleInputDigit1 = "ULL";
            string exampleInputDigit2 = "RRDDD";
            string exampleInputDigit3 = "LURDL";
            string exampleInputDigit4 = "UUUUD";

            Console.WriteLine("Example: ");
            Console.WriteLine(GetDigit(exampleInputDigit1));
            Console.WriteLine(GetDigit(exampleInputDigit2));
            Console.WriteLine(GetDigit(exampleInputDigit3));
            Console.WriteLine(GetDigit(exampleInputDigit4));

            CurrentPositionX = 1;
            CurrentPositionY = 1;

            string finalInputDigit1 = "";
            string finalInputDigit2 = "";
            string finalInputDigit3 = "";
            string finalInputDigit4 = "";
            string finalInputDigit5 = "";

            Console.WriteLine("Test: ");
            Console.WriteLine(GetDigit(finalInputDigit1));
            Console.WriteLine(GetDigit(finalInputDigit2));
            Console.WriteLine(GetDigit(finalInputDigit3));
            Console.WriteLine(GetDigit(finalInputDigit4));
            Console.WriteLine(GetDigit(finalInputDigit5));

            Console.ReadLine();
        }


        public static int GetDigit(string input)
        {
            foreach (char c in input)
            {
                switch (c)
                {
                    case 'L':
                        if (CurrentPositionX > 0)
                            CurrentPositionX--;
                        break;
                    case 'R':
                        if (CurrentPositionX < 2)
                            CurrentPositionX++;
                        break;
                    case 'U':
                        if (CurrentPositionY > 0)
                            CurrentPositionY--;
                        break;
                    case 'D':
                        if (CurrentPositionY < 2)
                            CurrentPositionY++;
                        break;
                }
            }

            return Keypad[CurrentPositionY][CurrentPositionX];
        }
    }
}
